export default {
	getReviewerSelectData: () => {
		//const empData= getEmployees.data.map(employee=>({label:employee.name, value:employee.id}))
		let empData= [];
		const uniqueIds = [];
			getEmployees.data.map(employee=>{
		  getEmployees.data.map(emp=>{
				if(employee.id===emp.reviewer){
					empData=[...empData,{label:employee.name, value:employee.name}];
				}
			})
		});
		console.log("empDataempData",empData)

		const uniqueReviewers = empData.filter(element => {
        const isDuplicate = uniqueIds.includes(element.value);
        if (!isDuplicate) {
           uniqueIds.push(element.value);
           return true;
        }
        return false;
    });
		return uniqueReviewers;
	},
	onSuccessfulCreate: () => {
		getEmployees.run(() => {
			showAlert("Created Successfully", 'success');
			closeModal('createEmployee');
		});
	},
	onSuccessfulUpdate: () => {
		getEmployees.run(() => {
			showAlert("Updated Successfully", 'success');
			closeModal('updateEmployee');
		});
	},
	onDelete: () => {
		getReviewers.run()
		const isReviewer = getReviewers.data.find((reviewer) => reviewer.empid == employeeTable.selectedRow.empid)
		if(isReviewer === undefined){
			deleteEmployee.run();
			getEmployees.run();
			showAlert("Deleted Successfully", 'success');
			closeModal('deleteEmpModal');
		}
		else if(isReviewer !== undefined){
			showAlert("Delete records that is that the selected data is referenced in", 'error');
			closeModal('deleteEmpModal');
		}
		else {
			showAlert("Failed to delete", 'error');
			closeModal('deleteEmpModal');
		}
	},
}
