() => {
  const currentFeedBack = reviewerObj.feedbackId;
  const reviewerId = reviewerName.selectedOptionValue;
  const selectedCategory = kraCategories.selectedOptionValue;
  const feedbacks = getFeedback.data.filter(fdbk => fdbk.from_employee === reviewerId && fdbk.to_employee === toName.selectedOptionValue && fdbk.category === selectedCategory);
  if (reviewerFeed_continue.text.length == 0 && reviewerFeed_moreOf.text.length == 0) return true;
  if (reviewerId.length === 0 || toName.selectedOptionValue.length === 0 || selectedCategory.length === 0) return true;
  if (feedbacks.length === 0 && reviewerFeed_continue.text.length > 0 && reviewerFeed_moreOf.text.length > 0) return false; else return true;
}

