() => {
  const feedbackList = getFeedback.data;
  const toId = toName.selectedOptionValue;
  const categories = getKraCategories.data;
  const reviewerId = reviewerName.selectedOptionValue;
  const feedback = feedbackList.map(feedback => {
    if (feedback.from_employee === reviewerId && feedback.to_employee === toId && ["reviewer"].includes(feedback.feedback_type)) {
      return {
        ...feedback,
        category: categories.find(category => category.id === feedback.category).name
      };
    }
  }).filter(e => e);
  console.log("feedbackfeedback", feedback[0], reviewerObj.feedbackId);
  return feedback;
}

