() => {
  const currentFeedBack = KRACategoryList.selectedRow.id;
  const selfId = selfName.selectedOptionValue;
  console.log("feedbacfeedbacksksselfId", selfId);
  if (selfId.length == 0) {
    return true;
  }
  if (ContDoInput.text.length == 0 || candoInput.text.length == 0) {
    return true;
  }
  console.log("isFeedbackPostedoutt", currentFeedBack == undefined, currentFeedBack.length == 0);
  if (currentFeedBack == undefined || currentFeedBack.length == 0) {
    return true;
  } else {
    const feedbacks = getFeedback.data.filter(fdbk => fdbk.from_employee == selfName.selectedOptionValue && fdbk.feedback_type == "self");
    const isFeedbackPosted = feedbacks.some(feedback => feedback.category == currentFeedBack);
    console.log("isFeedbackPosted", isFeedbackPosted);
    return !isFeedbackPosted;
  }
}

